import { motion, useAnimation } from 'framer-motion';
import { useState } from 'react';
import './App.css';
import moving from './static/Animations';

function App() {
  const [pos, setPos] = useState(0, 0);
  const animateMove = useAnimation();
  // const object = window.document.getElementById('helloWorld');

  const height = window.innerHeight;
  const width = window.innerWidth;
  setPos(width, height);
  console.log(pos);

  animateMove.start(moving(width, height));

  return (
    <div className='App'>
      <motion.div className='' id='helloWorld' animate={animateMove}>
        <h1>Hello World</h1>
      </motion.div>
    </div>
  );
}

export default App;
