import { motion, useAnimation } from 'framer-motion';
import './App.css';

function App() {
  const animateMove = useAnimation();
  const helloWorld = document.getElementById('#helloWorld');
  console.log(helloWorld.style);

  return (
    <div className='App'>
      <motion.div
        className='rainbow'
        id='helloWorld'
        initial={{ x: 0 }}
        animate={animateMove}
      >
        <h1>Hello World</h1>
      </motion.div>
    </div>
  );
}

export default App;
