import { motion, useAnimation } from 'framer-motion';
import './App.css';

function App() {
  const animateMove = useAnimation();

  return (
    <div className='App'>
      <motion.div
        className='rainbow'
        animate={{
          x: this.x + 1,
          transition: {
            delay: 2,
          },
        }}
      >
        <h1>Hello World</h1>
      </motion.div>
    </div>
  );
}

export default App;
