import { motion, useAnimation } from 'framer-motion';
import { useEffect, useState } from 'react';
import './App.css';
import moving from './static/Animations';

function App() {
  const [posX, setPosX] = useState(0);
  const [posY, setPosY] = useState(0);
  useEffect(() => {
    console.log(posX, posY);
  }, [posX, posY]);
  const animateMove = useAnimation();
  const y = window.innerHeight;
  const x = window.innerWidth;

  if (posX <= 100) {
    setPosX(posX + 1);
  }

  return (
    <div className='App'>
      <motion.div className='' id='helloWorld' animate={animateMove}>
        <h1>Hello World {posX}</h1>
      </motion.div>
    </div>
  );
}

export default App;
